namespace Mageplaza\BannerSlider\Setup\Patch\Schema;

use Magento\Framework\DB\Ddl\Table;
use Magento\Framework\Setup\Patch\SchemaPatchInterface;
use Magento\Framework\Setup\ModuleDataSetupInterface;

class AddCategoryColumnPatch implements SchemaPatchInterface
{
    /**
     * @var ModuleDataSetupInterface
     */
    private $moduleDataSetup;

    /**
     * @param ModuleDataSetupInterface $moduleDataSetup
     */
    public function __construct(
        ModuleDataSetupInterface $moduleDataSetup
    ) {
        $this->moduleDataSetup = $moduleDataSetup;
    }

    /**
     * {@inheritdoc}
     */
    public function apply()
    {
        $this->moduleDataSetup->startSetup();
        $connection = $this->moduleDataSetup->getConnection();
        
        $tableName = $this->moduleDataSetup->getTable('mageplaza_bannerslider_banner');
        
        // Check if column exists before adding
        if (!$connection->tableColumnExists($tableName, 'category')) {
            $connection->addColumn(
                $tableName,
                'category',
                [
                    'type' => Table::TYPE_SMALLINT,
                    'nullable' => true, 
                    'default' => 0,
                    'comment' => 'Banner Category (0:Website, 1:Mobile)'
                ]
            );
        }
        
        $this->moduleDataSetup->endSetup();
        
        return $this;
    }

    /**
     * {@inheritdoc}
     */
    public static function getDependencies()
    {
        return [];
    }

    /**
     * {@inheritdoc}
     */
    public function getAliases()
    {
        return [];
    }
}